import React from 'react';
import socketIO from 'socket.io-client';
const io = socketIO('localhost:3000/')

export default class Comment extends React.Component{
  render(){
      return(
          <div>
              <form onSubmit = {this.props.reaction}>
              <input id = "handle" type = "text" placeholder = "Handle"></input><br/>
              <input id = "message" type = "text" placeholder = "Message" onKeyDown = {this.props.feedback}></input>
              <button>Send</button>
              </form>
              {this.props.comment.length >this.props.displayComments}
              <div id=  "feedback">{this.props.displayFeedback}</div>
          </div>
      )
  }
}
