import React from 'react';
import { ReactMic } from 'react-mic';
import AudioContext from './AudioContext'
export default class Recorder extends React.Component {
    state = {
        record: false,
        audioSrc: ""
    }

    startRecording = () => {
        this.setState({
          record: true
        });
      }

      stopRecording = () => {
        this.setState({
          record: false
        });
      }

      onData(recordedBlob) {
        console.log('chunk of real-time data is: ', recordedBlob);
      }

      onStop(recordedBlob) {
        console.log('recordedBlob is: ', recordedBlob);
        this.setState({

            audioSrc: recordedBlob
        })
      }



    render(){
        console.log(this.state.audioSrc)
        return(
           <div>
           <ReactMic record={this.state.record} className="sound-wave" onStop={this.onStop}
            strokeColor="#000000" backgroundColor="#FF4081" nonstop={true} duration={5} />
            <button onClick={this.startRecording} type="button">Start</button>
            <button onClick={this.stopRecording} type="button">Stop</button>
            <article class="clip">
            <audio controls src = {this.state.audioSrc}></audio>
            <button>Delete</button>
            </article>
           </div>
        )
    }
}

