import React from 'react';
import { ReactMic } from 'react-mic';
// import AudioContext from './AudioContext';
// import AudioPlay from './AudioPlay'
export default class Recorder extends React.Component {
    state = {
        record: false
    }

    startRecording = () => {
        this.setState({
          record: true
        });
      }

      stopRecording = () => {
        this.setState({
          record: false
        });
        console.log('hi')
      }

      onData = (recordedBlob)=> {
        console.log('chunk of real-time data is: ', recordedBlob);
        fetch(recordedBlob)
        .then(res => res.any)
      }

      onStop = (recordedBlob)=> {
        console.log('recordedBlob is: ', recordedBlob);
        this.setState({
            record:false,
            blobURL: recordedBlob.blobURL
        })

      }

     play = () =>{
       console.log('you reached me')
       const audio = new Audio(this.state.audioSrc)
       audio.play()       
     }

    render(){
      const {blobURL} = this.state
        return(
           <div>
           <ReactMic record={this.state.record} className="sound-wave" onStop={this.onStop}
            strokeColor="#000000" backgroundColor="#FF4081" nonstop={true} duration={5} />
            <button onClick={this.startRecording} type="button">Start</button>
            <button onClick={this.stopRecording} type="button">Stop</button>
            <button onClick={this.play} type="button">Play</button>
            <article class="clip">
            <audio ref = "audioSource" controls = "controls" src = {blobURL} ></audio>
            <button>Delete</button>
            </article>
           </div>
        )
    }
}

