import React from 'react';
import SearchBar from './Component/SearchBar'
import SongList from './Component/SongList'
import Recorder from './Component/Recorder'
import Comment from './Component/Comment'
import socketIO from 'socket.io-client';
const io = socketIO('localhost:3000/')

class App extends React.Component {
  state = {
    songIds:[],
    record:false,
    messages:[]
  }

  // pass videoIds to songList 
   songList = (songIds) =>{
    this.setState({
      songIds:songIds
    })
  }

  reaction = (e) => {
    e.preventDefault()
    io.emit('comment', {
       message: e.target.children[2].value,
       handle: e.target.children[0].value
    })

    //console.log(e.target.children[0].value)
     io.on('comment', messageData => {
        // let chat = document.getElementById('output')
        // chat.innerHTML += `${messageData.handle}:${messageData.message}`
      this.displayReaction(messageData)     
     })

  }

  displayComments = (messageData) =>{
    let chat = document.getElementById('output')
    chat.innerHTML += '<p>' + messageData.handle + ':' + messageData.message + '</p>'//`${messageData.handle}:${messageData.message}`
  }

  render(){
    return(
         <div>
            <SearchBar songList = {this.songList}/>
            <SongList songIds = {this.state.songIds}/>
            <Recorder />
            <Comment reaction = {this.reaction} displayComments = {this.displayComments}/>
         </div>

    )
  }
}


export default App;
