import React from 'react';
import SearchBar from './Component/SearchBar'
import SongList from './Component/SongList'
import Recorder from './Component/Recorder'
import Comment from './Component/Comment'
import socketIO from 'socket.io-client';
const io = socketIO('localhost:3000/')

class App extends React.Component {
  state = {
    songIds:[],
    record:false,
    handle:"",
    comment:"",
    comments:[]
  }

  // pass videoIds to songList 
   songList = (songIds) =>{
    this.setState({
      songIds:songIds
    })
  }

  reaction = (e) => {
    e.preventDefault()
    io.emit('comment')
    // io.emit('comment',  {
    //    message: e.target.children[2].value,
    //    handle: e.target.children[0].value
    // })
    //console.log(e.target.children[0].value)
     io.on('comment', messageData => {
      this.displayComments(messageData)     
     })
  }

  displayComments = (messageData) =>{
    let comments = this.state.comments
        comments.push(messageData)
    this.setState({
       comments: comments
    })
    // let chat = document.getElementById('output')
    // chat.innerHTML += '<p>' + messageData.handle + ':' + messageData.message + '</p>'
  }

  feedback = () =>{
    //io.emit('typing', ()=>{ })
    let name = this.state.comments[this.state.comments.length -1].handle
   this.setState({
     feedback: `${name} is typing ...`
   })
  }

  render(){
    return(
         <div>
            <SearchBar songList = {this.songList}/>
            <SongList songIds = {this.state.songIds}/>
            <Recorder />
            <Comment reaction = {this.reaction} displayComments = {this.state.comments} feedback = {this.feedback} displayFeedback = {this.state.feedback}/>
         </div>

    )
  }
}


export default App;
