import React from 'react';


export default class Main extends React.Component{
    handleSubmit = (e) =>{
        e.preventDefault()
        let query = (e.target.children[0].value).toLowerCase()
        const URL = `https://www.googleapis.com/youtube/v3/search?part=snippet&channelId=UCwTRjvjVge51X-ILJ4i22ew&q=${query}&key=AIzaSyBlG_6DX-JMovaElRRI4KqXl_k0KAyHH_0`
        fetch(URL)
        .then((res) => res.json())
        .then(songData => console.log(songData.items.filter(song => song.id.videoId !==une )))
    }

 render(){
     return(
         <div>
             <form onSubmit = {this.handleSubmit}>
                 <input type = "text" placeholder = "Your next song is..."/>
                 <input type = "submit"/>
             </form>
         </div>
     )
 }
}

